mapper.select:
select a.EMP_ID, a.EMP_PASSWORD, a.EMP_NM, a.EMP_PASSWORD, a.TELNO, a.SEXDSTN, a.PSPOFC, a.SALARY, a.CAREER, a.REGIST_ID, a.REGIST_DT, a.UPDT_ID, a.UPDT_DT, a.FILE_ID from t_emp_m a
WHERE EMP_NM LIKE CONCAT('%', #{searchKeyword},'%') OR EMP_ID LIKE CONCAT('%', #{searchKeyword},'%')
ORDER BY REGIST_DT ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT SN_KEY
FROM t_sn_m
WHERE SN_KEY =#{snKey}
